pragma solidity ^0.5.0;

import "./IInflationaryToken.sol";
import "openzeppelin-eth/contracts/token/ERC20/ERC20Mintable.sol";
import "zos-lib/contracts/Initializable.sol";

/**
 * @title RelevantCoin
 */

contract InflationaryToken is Initializable, IInflationaryToken, ERC20Mintable {

    string public name;
    uint8 public decimals;
    string public symbol;
    string public version;
    uint16 public inflationRate;

    function initialize(
        string memory _name, 
        uint8 _decimals, 
        string memory _symbol, 
        string memory _version, 
        uint16 _inflationRate
    )   public 
        initializer 
        returns(uint16 inflation) 
    {
        name = _name;
        decimals = _decimals;
        symbol = _symbol;
        version = _version;
        inflationRate = _inflationRate;
        return inflationRate;
    }
}
